-- Create "organizations" table
CREATE TABLE "public"."organizations" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "name" character varying NOT NULL, "code" character varying NOT NULL, "logo_url" character varying NULL, "address" character varying NULL, "phone" character varying NULL, "email" character varying NULL, "website" character varying NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "parent_id" bigint NULL, PRIMARY KEY ("id"), CONSTRAINT "organizations_organizations_children" FOREIGN KEY ("parent_id") REFERENCES "public"."organizations" ("id") ON UPDATE NO ACTION ON DELETE SET NULL);
-- Create index "organizations_code_key" to table: "organizations"
CREATE UNIQUE INDEX "organizations_code_key" ON "public"."organizations" ("code");
-- Create "departments" table
CREATE TABLE "public"."departments" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "name" character varying NOT NULL, "code" character varying NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "org_id" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "departments_organizations_departments" FOREIGN KEY ("org_id") REFERENCES "public"."organizations" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create index "department_code_org_id" to table: "departments"
CREATE UNIQUE INDEX "department_code_org_id" ON "public"."departments" ("code", "org_id");
-- Create "positions" table
CREATE TABLE "public"."positions" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "name" character varying NOT NULL, "code" character varying NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "department_id" bigint NOT NULL, "parent_id" bigint NULL, PRIMARY KEY ("id"), CONSTRAINT "positions_departments_positions" FOREIGN KEY ("department_id") REFERENCES "public"."departments" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "positions_positions_children" FOREIGN KEY ("parent_id") REFERENCES "public"."positions" ("id") ON UPDATE NO ACTION ON DELETE SET NULL);
-- Create index "position_department_id_code" to table: "positions"
CREATE UNIQUE INDEX "position_department_id_code" ON "public"."positions" ("department_id", "code");
-- Create "employees" table
CREATE TABLE "public"."employees" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "user_id" character varying NULL, "code" character varying NOT NULL, "status" character varying NOT NULL DEFAULT 'active', "joining_at" timestamptz NOT NULL, "org_id" bigint NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "position_id" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "employees_positions_employees" FOREIGN KEY ("position_id") REFERENCES "public"."positions" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create index "employees_code_key" to table: "employees"
CREATE UNIQUE INDEX "employees_code_key" ON "public"."employees" ("code");
-- Create index "employees_user_id_key" to table: "employees"
CREATE UNIQUE INDEX "employees_user_id_key" ON "public"."employees" ("user_id");
-- Create "labels" table
CREATE TABLE "public"."labels" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "name" character varying NOT NULL, "description" character varying NULL, "color" character varying NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "org_id" bigint NULL, PRIMARY KEY ("id"), CONSTRAINT "labels_organizations_labels" FOREIGN KEY ("org_id") REFERENCES "public"."organizations" ("id") ON UPDATE NO ACTION ON DELETE SET NULL);
-- Create index "label_org_id" to table: "labels"
CREATE INDEX "label_org_id" ON "public"."labels" ("org_id");
-- Create "leave_requests" table
CREATE TABLE "public"."leave_requests" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "total_days" double precision NOT NULL, "start_at" timestamptz NOT NULL, "end_at" timestamptz NOT NULL, "reason" character varying NULL, "type" character varying NOT NULL DEFAULT 'annual', "status" character varying NOT NULL DEFAULT 'pending', "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "employee_id" bigint NOT NULL, "org_id" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "leave_requests_employees_leave_requests" FOREIGN KEY ("employee_id") REFERENCES "public"."employees" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "leave_requests_organizations_leave_requests" FOREIGN KEY ("org_id") REFERENCES "public"."organizations" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "leave_approvals" table
CREATE TABLE "public"."leave_approvals" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "comment" character varying NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "reviewer_id" bigint NOT NULL, "leave_request_id" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "leave_approvals_employees_leave_approves" FOREIGN KEY ("reviewer_id") REFERENCES "public"."employees" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "leave_approvals_leave_requests_leave_approves" FOREIGN KEY ("leave_request_id") REFERENCES "public"."leave_requests" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "projects" table
CREATE TABLE "public"."projects" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "name" character varying NOT NULL, "code" character varying NOT NULL, "description" character varying NULL, "start_at" timestamptz NULL, "end_at" timestamptz NULL, "process" bigint NULL, "status" character varying NOT NULL DEFAULT 'not_started', "visibility" character varying NOT NULL DEFAULT 'private', "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "creator_id" bigint NOT NULL, "updater_id" bigint NOT NULL, "org_id" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "projects_employees_created_projects" FOREIGN KEY ("creator_id") REFERENCES "public"."employees" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "projects_employees_updated_projects" FOREIGN KEY ("updater_id") REFERENCES "public"."employees" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION, CONSTRAINT "projects_organizations_projects" FOREIGN KEY ("org_id") REFERENCES "public"."organizations" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
-- Create "project_members" table
CREATE TABLE "public"."project_members" ("project_id" bigint NOT NULL, "employee_id" bigint NOT NULL, PRIMARY KEY ("project_id", "employee_id"), CONSTRAINT "project_members_employee_id" FOREIGN KEY ("employee_id") REFERENCES "public"."employees" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "project_members_project_id" FOREIGN KEY ("project_id") REFERENCES "public"."projects" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
-- Create "tasks" table
CREATE TABLE "public"."tasks" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "name" character varying NOT NULL, "code" character varying NOT NULL, "description" character varying NULL, "process" bigint NOT NULL DEFAULT 0, "status" character varying NOT NULL DEFAULT 'not_received', "start_at" timestamptz NULL, "due_date" timestamptz NULL, "creator_id" bigint NOT NULL, "updater_id" bigint NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "type" character varying NOT NULL DEFAULT 'task', "project_id" bigint NULL, PRIMARY KEY ("id"), CONSTRAINT "tasks_projects_tasks" FOREIGN KEY ("project_id") REFERENCES "public"."projects" ("id") ON UPDATE NO ACTION ON DELETE SET NULL);
-- Create index "tasks_code_key" to table: "tasks"
CREATE UNIQUE INDEX "tasks_code_key" ON "public"."tasks" ("code");
-- Create "task_assignees" table
CREATE TABLE "public"."task_assignees" ("task_id" bigint NOT NULL, "employee_id" bigint NOT NULL, PRIMARY KEY ("task_id", "employee_id"), CONSTRAINT "task_assignees_employee_id" FOREIGN KEY ("employee_id") REFERENCES "public"."employees" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "task_assignees_task_id" FOREIGN KEY ("task_id") REFERENCES "public"."tasks" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
-- Create "task_labels" table
CREATE TABLE "public"."task_labels" ("task_id" bigint NOT NULL, "label_id" bigint NOT NULL, PRIMARY KEY ("task_id", "label_id"), CONSTRAINT "task_labels_label_id" FOREIGN KEY ("label_id") REFERENCES "public"."labels" ("id") ON UPDATE NO ACTION ON DELETE CASCADE, CONSTRAINT "task_labels_task_id" FOREIGN KEY ("task_id") REFERENCES "public"."tasks" ("id") ON UPDATE NO ACTION ON DELETE CASCADE);
