-- Create "appointment_histories" table
CREATE TABLE "public"."appointment_histories" ("id" bigint NOT NULL GENERATED BY DEFAULT AS IDENTITY, "position_name" character varying NOT NULL, "joining_at" timestamptz NOT NULL, "description" character varying NULL, "attachment_urls" character varying NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "employee_id" bigint NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "appointment_histories_employees_appointment_histories" FOREIGN KEY ("employee_id") REFERENCES "public"."employees" ("id") ON UPDATE NO ACTION ON DELETE NO ACTION);
